function draw()
{
    myCanvas.mouseOver(BoardObj.hovered);

    myCanvas.mouseOut(BoardObj.hoveredOut);
    
    BoardObj.drawBoard();

    //function to detect Hover
    BoardObj.highlightCellonHover();

    
    
}


